mapper.select:
select

from tb_receipt_pool_assest_dtl
where 1=1

	and bill_currency_type= #{currencyType}


	and business_unit_id= #{businessUnitId}

order by business_date desc

select count(*) from tb_receipt_pool_assest_dtl where 1=1
and bill_currency_type= #{currencyType}
and business_unit_id= #{businessUnitId}
order by business_date desc
